import React, { useEffect, useState } from "react";
import axios from "axios"
import Country from "../country/country.component";

const SearchBar = () => {

  const [input, setInput] = useState()
  const [allCountries, setAllCountries] = useState()

  const handleChange = e => {
    setInput(e.target.value)
  }

  useEffect(() => {
    axios
      .get("https://restcountries.com/v3.1/all")
      .then(res => {
        console.log("Promise Passed!");
        const data = res.data.map(datum => datum.name.common)
        setAllCountries(data)
      })
  }, [])

  const filteredResult = allCountries.filter(country => country.toLowerCase().includes(input.toLowerCase())).map((filtered, index) => (
    // <Country 
    //   key={index} 
    //   name={filtered}
    //   officialName={filtered}
    //   capital={filtered}
    //   independent={filtered}
    //   subregion={filtered}
    //   region={filtered}
    //   population={filtered}
    //   flags={filtered}
    //   continents={filtered}
    // />
    filter
  ))


  return(
    <div>
      <input
        type="text"
        value={input}
        onChange={handleChange}
      />
      <div>
        {filteredResult}
      </div>
    </div>
  )
}

export default SearchBar
