import React, { useEffect, useState } from "react";
import axios from "axios"
import Country from "../country/country.component";

const SearchBar = () => {

  const [input, setInput] = useState()
  const [allCountries, setAllCountries] = useState()

  const handleChange = e => {
    setInput(e.target.value)
  }

  useEffect(() => {
    axios
      .get("https://restcountries.com/v3.1/all")
      .then(res => {
        console.log("Promise Passed!");
        const data = res.data.map(datum => datum.name.common)
        setAllCountries(data)
      })
  }, [])

  const filteredResult = allCountries.filter(country => country.includes(input)).map(filteredCountry => (
      <Country 
        name={filteredCountry}
        officialName={filteredCountry}
        capital={filteredCountry}
        independent={filteredCountry}
        subregion={filteredCountry}
        region={filteredCountry}
        population={filteredCountry}
      />
    ))

  return(
    <div>
      <input
        type="text"
        value={input}
        onChange={handleChange}
      />
      {filteredResult}
    </div>
  )
}

export default SearchBar
